import * as api from '../api';

const createAppointment = (newAppointment) => async (dispatch) => {
    try {
        console.log(newAppointment);
        const { newAppointment } = await api.createAppointment(newAppointment);
        dispatch ({ type: 'CREATE' , payload: newAppointment });
    } catch (error) {
        console.log(error.message)
    }
}

export default createAppointment;
